def generate_csv():
    print(f"üöÄ ‡∏Å‡∏≥‡∏•‡∏±‡∏á‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤ White Rose ‡∏à‡∏≥‡∏ô‡∏ß‡∏ô {TOTAL_PRODUCTS:,} ‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£...") 
    
    with open(FILENAME, mode='w', newline='', encoding='utf-8') as file:
        writer = csv.writer(file)
        # ‡πÄ‡∏Ç‡∏µ‡∏¢‡∏ô Header
        writer.writerow(["id", "title", "description", "category", "price"])
        
        for i in range(1, TOTAL_PRODUCTS + 1):
            cat_name = random.choice(list(categories.keys()))
            data = categories[cat_name]
            
            brand = random.choice(data["brands"])
            product = random.choice(data["products"])
            adj = random.choice(data["adjectives"])
            size = random.choice(data["sizes"])
            template = random.choice(desc_templates)
            
            # ‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏ä‡∏∑‡πà‡∏≠‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤ (Title)
            title = f"{product} {brand} {adj} {size}"
            
            # ‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏Ñ‡∏≥‡∏ö‡∏£‡∏£‡∏¢‡∏≤‡∏¢ (Description) ‡πÉ‡∏™‡πà Keyword ‡πÄ‡∏¢‡∏≠‡∏∞‡πÜ
            description = f"{template} ({product} {adj} ‡∏¢‡∏µ‡πà‡∏´‡πâ‡∏≠ {brand} ‡∏Ç‡∏ô‡∏≤‡∏î {size}) - ‡∏´‡∏°‡∏ß‡∏î‡∏´‡∏°‡∏π‡πà: {cat_name}"
            
            # Logic ‡∏£‡∏≤‡∏Ñ‡∏≤‡∏™‡∏°‡∏à‡∏£‡∏¥‡∏á (‡∏•‡∏á‡∏ó‡πâ‡∏≤‡∏¢‡∏î‡πâ‡∏ß‡∏¢ 9)
            base_price = random.randint(20, 1000)
            if cat_name in ["Fresh Food", "Instant Food", "Snacks"]: base_price = random.randint(15, 250)
            elif cat_name in ["Mom & Baby"]: base_price = random.randint(300, 1500)
            
            price = (base_price // 5) * 5 + 9 
            if price <= 0: price = 9

            writer.writerow([20000 + i, title, description, cat_name, price])
            
            if i % 5000 == 0:
                print(f"   ...‡∏™‡∏£‡πâ‡∏≤‡∏á‡πÅ‡∏•‡πâ‡∏ß {i:,} ‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£")
            
    print(f"‚úÖ ‡πÄ‡∏£‡∏µ‡∏¢‡∏ö‡∏£‡πâ‡∏≠‡∏¢! ‡∏™‡∏£‡πâ‡∏≤‡∏á‡πÑ‡∏ü‡∏•‡πå '{FILENAME}' ‡∏à‡∏≥‡∏ô‡∏ß‡∏ô {TOTAL_PRODUCTS:,} ‡πÅ‡∏ñ‡∏ß‡πÄ‡∏™‡∏£‡πá‡∏à‡πÅ‡∏•‡πâ‡∏ß")
    print(f"üëâ ‡∏Ç‡∏±‡πâ‡∏ô‡∏ï‡∏≠‡∏ô‡∏ï‡πà‡∏≠‡πÑ‡∏õ: ‡∏£‡∏±‡∏ô 'python import_white_rose.py' ‡πÄ‡∏û‡∏∑‡πà‡∏≠‡∏ô‡∏≥‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÄ‡∏Ç‡πâ‡∏≤‡∏ï‡∏π‡πâ‡πÄ‡∏ã‡∏ü")

